import {React,useState} from "react";
import Dropdown from "./DropDown";
import Timer from "./Timer"
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faRotateLeft, faLightbulb,faFacethinking,faSkull } from '@fortawesome/free-solid-svg-icons'

//  <FontAwesomeIcon icon={faLightbulb} />

function Controls(props){
  const [iconActive,SetIconActive] = useState(false);
  
  

  async function handleDifficulty(e){
    // using async function to get SetDifficultyLevel to change so it triggers a new generateBoard() in SudokuBrain
    if(e.target.id === "easy"){
      await props.SetDifficultyLevel(0);
      props.SetDifficultyLevel(1);
    } else if (e.target.id === "medium"){
      await props.SetDifficultyLevel(0);
      props.SetDifficultyLevel(2);
    } else if(e.target.id === "hard"){
      await props.SetDifficultyLevel(0);
      props.SetDifficultyLevel(3);

    }
    console.log(props.difficultyLevel)
    
  }

  function handleNumberClick(e){
    
    let ref = props.lastRef.current;
   
     if(isNaN(parseInt(props.liveBoard[ref.id])) && e.target.innerText !== "erase"){
      props.lastRef.current.innerText = e.target.innerText;
    }
      props.lastRef.current.focus()
    props.checkSolution();
  }

  function undo(e){
    // Animation for undo Arrow
    let arrow = document.querySelector(".undoButton .icon");
    arrow.classList.toggle("arrowTransition")
    setTimeout(function(){ arrow.classList.toggle("arrowTransition"); }, 2000);

    //Check if focused sudoku blocked is part of the original puzzle, then cant be changed
    let ref = props.lastRef.current;

    if(parseInt(ref.innerText) !== props.liveBoard[ref.id]){
      props.lastRef.current.innerText = ""
    }
    props.lastRef.current.focus()
    props.checkSolution();
  }

  function hint(e){
    // Animation for LightBulb
    let bulb = document.querySelector(".lightBulb .icon");
    bulb.classList.toggle("bulbTransition");
    setTimeout(function(){
      bulb.classList.toggle("bulbTransition"); 
    }, 2000);
    
    // Get hint from the original puzzle
    let ref = props.lastRef.current;
      console.log(props.solvedBoard.flat());
      let solvedBoardFlat = props.solvedBoard.flat();
      if(parseInt(ref.innerText) !== props.liveBoard[ref.id]){
        props.lastRef.current.innerText = solvedBoardFlat[ref.id]
      }

    
    props.lastRef.current.focus()
    props.checkSolution();
  }

  function solve(){
    // Animaton for Solved button
    //let solveIcon = document.querySelector(".solveIcon .icon");
    //solveIcon.classList.toggle("solveTransition");
    //setTimeout(function(){
    //  solveIcon.classList.toggle("bulbTransition"); 
    //}, 2000);
   
    let solvedBoardFlat = props.solvedBoard.flat();
    const tempArr = document.getElementsByClassName("SudokuBlock");
    // set the innerText to the solved board, so the checkSoulution has a full innertext board to work with
    for(let i = 0; i < solvedBoardFlat.length; i++){
      
      tempArr[i].innerText = solvedBoardFlat[i];
      
     
    }
    props.lastRef.current.focus()
   
    
    props.checkSolution();
  }

  function skull(e){
    // try to change icon to skull if clicked and solved
   let solveFace = document.querySelector("solveFace");
   if(props.isSolved){
     return(
      <FontAwesomeIcon className="icon solveFace" icon={faSkull} />
     )
   } else {
     return (
<FontAwesomeIcon icon="fa-light fa-face-thinking" />
     )
   }
  
  }

 let faceIcon = skull();

  function inputKeys(){
    return(
      <div className="inputContainer">
        <div value="1" className="inputKey" onClick={handleNumberClick}>1</div>
        <div className="inputKey" onClick={handleNumberClick}>2</div>
        <div className="inputKey" onClick={handleNumberClick}>3</div>
        <div className="inputKey" onClick={handleNumberClick}>4</div>
        <div className="inputKey" onClick={handleNumberClick}>5</div>
        <div className="inputKey" onClick={handleNumberClick}>6</div>
        <div className="inputKey" onClick={handleNumberClick}>7</div>
        <div className="inputKey" onClick={handleNumberClick}>8</div>
        <div className="inputKey" onClick={handleNumberClick}>9</div>
        <div  className="inputKey undoButton" onClick={undo}><FontAwesomeIcon className="icon" icon={faRotateLeft} /><span className="iconButtons" >Undo</span></div>
        <div className="inputKey lightBulb" onClick={hint}> <FontAwesomeIcon className="icon" icon={faLightbulb}/><span className="iconButtons">Hint</span></div>
        <div className="inputKey solveIcon" onClick={solve}>{faceIcon}<span className="iconButtons">Solve</span></div>
      </div>
    )
  }
  let inputkeyList = inputKeys();
  return(
    <div className="controlContainer">
    <div className="dropdownContainer">
    <Dropdown handleDifficulty={handleDifficulty}/>
  </div>  
  
  <Timer 
  SetScoreCard={props.SetScoreCard}
  scoreCard={props.scoreCard}
  isSolved={props.isSolved}
  switchTheme={props.switchTheme}
  difficultyLevel={props.difficultyLevel}

  />
    {inputkeyList}
    </div>
  )
}

export default Controls;
