import { logDOM } from "@testing-library/react";
import {React,useState,useEffect,useContext} from "react";
import { UserContext } from "../userContext";


function Dropdown() {
  const {isActive,setIsActive,SetSolvedBoard,isSolved,SetIsSolved,SetDifficultyLevel} = useContext(UserContext);
  const [close, SetClose] = useState(false);
  
  // Virkar ekki... búa til nýtt.
  function handleDomClick(){
    
    const DropdownDiv = document.getElementById("drop");
    
    document.addEventListener("click", (event) => {
      const isClickInside = DropdownDiv.contains(event.target);
      console.log(isClickInside)
    
      if (!isClickInside) {
        setIsActive(false)
      }
    });

    //const DifficultyPick = document.getElementsByClassName("dropdown-content");
    //document.addEventListener("click", (event) => {
    //})

  }

  async function handleDifficulty(e){
    // using async function to get SetDifficultyLevel to change so it triggers a new generateBoard() in SudokuBrain
    if(e.target.id === "easy"){
      await SetDifficultyLevel(0);
      SetDifficultyLevel(1);
    } else if (e.target.id === "medium"){
      await SetDifficultyLevel(0);
      SetDifficultyLevel(2);
    } else if(e.target.id === "hard"){
      await SetDifficultyLevel(0);
      SetDifficultyLevel(3);

    }
   
  }

  function handlePick(e){
    handleDifficulty(e)
    if(isSolved){
      SetSolvedBoard([]);
      SetIsSolved(current => !current)
    }
    setIsActive(!isActive)
  }
  
function handleIsActive(){
  console.log(isActive);
  setIsActive(!isActive)
}
  
  useEffect(()=>{
  //handleDomClick()
  const DropdownDiv = document.getElementById("ButtonA");
    
  document.addEventListener("click", (event) => {
    const isClickInside = DropdownDiv.contains(event.target);
    console.log(isClickInside)
  
    if (!isClickInside) {
      setIsActive(false)
    }
  });
  }, [])

  return (
    <div id="drop"  className='dropdown'>
    <div id="ButtonA" onClick={handleIsActive} className="dropdown-btn">
      New Game
    </div>
    { close && (
      <div className='dropdown-content'>
        <div id="easy" onClick={handlePick} className='dropdown-item'>
        Easy
        </div>
        <div id="medium" onClick={handlePick} className='dropdown-item'>
          Medium
        </div>
        <div id="hard" onClick={handlePick} className='dropdown-item'>
          Hard
        </div>
      </div>
    )}
    </div>
  );
}

export default Dropdown;
