import React, {useEffect, useState,useRef} from "react";
import './SudokuBoard.css';

function Board(props){
  const [state,SetState] = useState(false);
  let refs = useRef(null);
  refs.current = document.getElementsByClassName("SudokuBlock")[2];
  let liveBoard = [];
  let anwser = [...props.currentBoard] 
  
  
//  console.log("this is the current board sent from parent, built from suduko brain")
//console.log(props.currentBoard)
  
    
    
   //liveBoard = await[...tempBoard];
   function handleMovement (e,idx){
    let currentBox = document.getElementsByClassName("SudokuBlock")[idx];
    
    
    console.log(anwser)
    switch(true) {
    case e.keyCode === 38 && idx > 8:
      refs.current = document.getElementsByClassName("SudokuBlock")[idx - 9]
      refs.current.focus();
    break;
    case e.keyCode === 37 && idx > 0:
      refs.current = document.getElementsByClassName("SudokuBlock")[idx - 1]
      refs.current.focus();
    break;
    case e.keyCode === 39 && idx < 80:
      refs.current = document.getElementsByClassName("SudokuBlock")[idx + 1]
      refs.current.focus();
    break;
    case e.keyCode === 40 && idx < 72:
      refs.current = document.getElementsByClassName("SudokuBlock")[idx + 9]
      refs.current.focus();
    break;
    case e.key >= 1 && e.key <= 9 && (!currentBox.innerText || currentBox.innerText !== anwser.charAt(idx)):
      document.getElementsByClassName("SudokuBlock")[idx].innerText = e.key;
      
      anwser[idx] = parseInt(e.key);
      
      //anwser.replace(anwser[idx],"2")
      console.log(idx)
      console.log(anwser);
    break;
   // case e.keyCode === 46  && currentBox.innerText !== newarr[idx]:  
   case (e.keyCode === 46 || e.keyCode === 8)  && currentBox.innerText != anwser[idx]:  
   document.getElementsByClassName("SudokuBlock")[idx].innerText = '';
    break;
  default:
    console.log("wrong input")
}
}

  function printBoard(board){

    for(let i = 0; i < board.length; i++){
      for(let j =0; j < board[i].length; j++){
        
        if(board[i][j] === 0){ liveBoard.push('  ')}
        else {liveBoard.push(board[i][j])}
      }
    }

    return liveBoard.map((item, idx) => ( 
      <div
        key={idx}
        ref={refs[idx]}
        tabIndex='0'
        className='SudokuBlock'
        onMouseDown={test}
        onKeyDown={(e) => handleMovement(e,idx)}
        
      >
        {item}
      </div>
    ));
