    function renderToArr (board) {
  
    for(let i = 0; i < board.length; i++){
      for(let j =0; j < board[i].length; j++){
        
        if(board[i][j] === 0){ newarr.push('  ')}
        else {newarr.push(board[i][j])}
      }
    }

    
        
    return newarr.map((item, idx) => ( 
      <div
        key={idx}
        ref={refs.current[idx]}
        tabIndex='0'
        className='SudokuBlock'
        onKeyDown={(e) => handleMovement(e,idx)} 
      >
        {item}
      </div>
    ));
  };
    */
    // TODO
    // prenta út div fyrir hvert stak, og gera handleinput á það.


   // console.log(props.currentBoard);;
  }
   
//  useEffect(() =>{
//    
//    if(!state){
