//  }, [state]);
let board = printBoard(props.currentBoard)
function test(e){
//console.log(e.target.__reactFiber$8ru86mk7pnt.alternate.key);
console.log()
e.target.focus();
}
 //useEffect(() => refs[2].current && refs[2].current.focus());
useEffect(() => {

  if(liveBoard.length > 1){
    
  }
  console.log("liveBoard is up");
  refs = document.getElementsByClassName("SudokuBlock")[2];
  refs.current.focus();
},[liveBoard])
  return(
    <>
    <div className="BoardContainer">
      {board}
    </div>
    <button onClick={test}>clickme</button>


    </>
  );
}
export default Board;
