import {React,useState,useEffect} from "react";

function Dropdown(props) {
  const [isActive, setIsActive] = useState(false);

  

  function handleClick(){
    const DropdownDiv = document.getElementById("drop");
    document.addEventListener("click", (event) => {
      const isClickInside = DropdownDiv.contains(event.target);
    
      if (!isClickInside) {
        setIsActive(false)
      }
    });

    const DifficultyPick = document.getElementsByClassName("dropdown-content");
    document.addEventListener("click", (event) => {
      const isClicked = DifficultyPick.contains(event.target)
      if(!isClicked){
        setIsActive(false)
      }
    })

  }



  useEffect(()=>{
handleClick()
  }, [])

  return (
    <div id="drop" className='dropdown'>
    <div onClick={e => setIsActive(!isActive)} className="dropdown-btn">
      New Game
    </div>
    { isActive && (
      <div className='dropdown-content'>
        <div id="easy" onClick={props.handleDifficulty} className='dropdown-item'>
        Easy
        </div>
        <div id="medium" onClick={props.handleDifficulty} className='dropdown-item'>
          Medium
        </div>
        <div id="hard" onClick={props.handleDifficulty} className='dropdown-item'>
          Hard
        </div>
      </div>
    )}
    </div>
  );
}

export default Dropdown;
