import React, {useEffect, useState,useRef} from "react";
import './SudokuBoard.css';
import Controls from '../components/Controls'


function Board(props){
  const [state,SetState] = useState(false);
  const [lastActiveDiv,SetLastActiveDiv] = useState()
  const [userAnwser, SetUserAnwser] = useState(anwser)
  let refs = useRef(null);
  refs.current = document.getElementsByClassName("SudokuBlock")[2];
  let liveBoard = [];
  let anwser = []; 
  
  
  
 
  // Movement section: track user input and change div focus and insert or delete from innerText
   function handleMovement (e,idx){
    let currentBox = document.getElementsByClassName("SudokuBlock")[idx];
    switch(true) {
    case e.keyCode === 38 && idx > 8:
      refs.current = document.getElementsByClassName("SudokuBlock")[idx - 9]
      refs.current.focus();
    break;
    case e.keyCode === 37 && idx > 0:
      refs.current = document.getElementsByClassName("SudokuBlock")[idx - 1]
      refs.current.focus();
    break;
    case e.keyCode === 39 && idx < 80:
      refs.current = document.getElementsByClassName("SudokuBlock")[idx + 1]
      refs.current.focus();
    break;
    case e.keyCode === 40 && idx < 72:
      refs.current = document.getElementsByClassName("SudokuBlock")[idx + 9]
      refs.current.focus();
    break;
    case e.key >= 1 && e.key <= 9 && isNaN(parseInt(liveBoard[idx])):
      document.getElementsByClassName("SudokuBlock")[idx].innerText = e.key;
      anwser[idx] = parseInt(e.key);
      SetUserAnwser([userAnwser[idx] = parseInt(e.key)])
    break;
   case (e.keyCode === 46 || e.keyCode === 8)  && currentBox.innerText != liveBoard[idx]:  
   document.getElementsByClassName("SudokuBlock")[idx].innerText = '';
    break;
    
  default:
    console.log(e)
}
}

  function printBoard(board){

    for(let i = 0; i < board.length; i++){
      for(let j =0; j < board[i].length; j++){
        
        if(board[i][j] === 0){ liveBoard.push('  ')}
        else {liveBoard.push(board[i][j])}
      }
    }
      
      anwser = [...liveBoard];
     
    return liveBoard.map((item, idx) => ( 
      <div
        key={idx}
        ref={refs[idx]}
        tabIndex='0'
        className='SudokuBlock'
        onClick={(e) => test(e,idx)}
        onKeyDown={(e) => handleMovement(e,idx)}
        
      >
        {item}
      </div>
    ));
      
  }
   
let board = printBoard(props.currentBoard)

function test(e,idx){
  console.log(idx);
  //let x = e.target.__reactFiber$km7ltcxneuo.index
//console.log(x)
//let divIndex = e.target.__reactFiber$km7ltcxneuo.index;
refs.current = document.getElementsByClassName("SudokuBlock")[idx]
refs.current.focus();
}

function solve(){
 //let arr1 = [1,2,3]
 //let arr2 = [1,2,3]
 let solvedBoard = props.solvedBoard;
 solvedBoard = solvedBoard.flat()

  if (solvedBoard.length === anwser.length) {
    return solvedBoard.every((element, index) => {
      if (element === anwser[index]) {
        console.log("Sudoku board solved")
      }
    });
  } else {
    console.log("not solved");
  }
}



function handler(e){
  console.log(e.keyCode);
  //refs.current = e.target;
  //e.target.focus();
  //console.log(e)
  //let divIndex = e.path[0].__reactFiber$nv6lose87zk.key;
  //refs.current = document.getElementsByClassName("SudokuBlock")[divIndex];
  //refs.current.focus();
}

useEffect(() => {
//window.addEventListener("click", handler)
//window.addEventListener("click", event => {
//  console.log(event.keyCode);
//});
})

useEffect(() => {


  if(liveBoard.length > 1){
    console.log("liveBoard is up");
    refs.current = document.getElementsByClassName("SudokuBlock")[2];
    refs.current.focus();
  }
  
},[liveBoard])
  
return(
    <div className="BoardAndControllerWrap">
    <div className="BoardContainer">
      {board}
    </div>
    <div>
    <Controls
        SetDifficultyLevel={props.SetDifficultyLevel}
        lastRef={refs} 
      />
    </div>
   
    </div>
  );
}
export default Board;
