  function  boardLevel(){
    tempBoard = [];
    console.log("Temp");
    console.log(tempBoard)
    console.log("Temp");

    console.log(solvedBoard)
   tempBoard =  [...solvedBoard];
    console.log(tempBoard)

    if(props.difficultyLevel === 1){
      console.log("diff lvl is on 1 = easy")
