import {React,useState,useEffect} from "react";

function Dropdown(props) {
  const [isActive, setIsActive] = useState(false);

  

  function handleClick(){
    const DropdownDiv = document.getElementById("drop");
    document.addEventListener("click", (event) => {
      const isClickInside = DropdownDiv.contains(event.target);
    
      if (!isClickInside) {
        setIsActive(false)
      }
    });

  }

  function test(){
    
  }

  useEffect(()=>{
handleClick()
  }, [])
  return (
    <div id="drop" className='dropdown'>
    <div onClick={e => setIsActive(!isActive)} className="dropdown-btn">
      New Game
    </div>
    { isActive && (
      <div className='dropdown-content'>
        <div value="easy" onClick={props.handleClick} className='dropdown-item'>
        Easy
        </div>
        <div className='dropdown-item'>
          Medium
        </div>
        <div className='dropdown-item'>
          Hard
        </div>
      </div>
    )}
    </div>
  );
}

export default Dropdown;
