import React, {useEffect} from "react";

function Board(props){

  let liveBoard = [];

  function boardLevel (){
    let tempBoard = [...props.solvedBoard];
    console.log(tempBoard)
    console.log(tempBoard.length)
    //const randomIndex = Math.floor(Math.random() * arr.length);

    if(props.difficultyLevel === 1){
      console.log("diff lvl is on 1 = easy")
      for(let i = 0; i < 44; i++){
        const randomCol = Math.floor(Math.random() * tempBoard.length);
        const randomRow = Math.floor(Math.random() * tempBoard.length);
        
        if(tempBoard[randomCol][randomRow] !== 0){
          tempBoard[randomCol][randomRow] = 0;
        } else {
          i--
        }
      }
    } else if(props.difficultyLevel === 2){
      for(let i = 0; i < 49; i++){
        const randomCol = Math.floor(Math.random() * tempBoard.length);
        const randomRow = Math.floor(Math.random() * tempBoard.length);
        
        if(tempBoard[randomCol][randomRow] !== 0){
          tempBoard[randomCol][randomRow] = 0;
        } else {
          i--
        }
      }
    } else if(props.difficultyLevel === 3){
      for(let i = 0; i < 54; i++){
        const randomCol = Math.floor(Math.random() * tempBoard.length);
        const randomRow = Math.floor(Math.random() * tempBoard.length);
        
        if(tempBoard[randomCol][randomRow] !== 0){
          tempBoard[randomCol][randomRow] = 0;
        } else {
          i--
        }
      }
    }
    console.log("herna")
   console.log(tempBoard);
  }

  function printBoard(){
    console.log(boardLevel[0][0])
  }
   
  useEffect(() => {
    // üëáÔ∏è move condition in hook
    if(props.solvedBoard){
      boardLevel();
    }
  }, [props.solvedBoard]);

  return(
    <>
            <button onClick={boardLevel}>aaa</button>

      <button onClick={printBoard}>bbb</button>
    </>
  );
}
export default Board;
