}


function shuffle(numberArray) {
  // Fisher Yates Shuffle //

  let currentIndex = numberArray.length,  randomIndex;

  // While there remain elements to shuffle.
  while (currentIndex !== 0) {

    // Pick a remaining element.
    randomIndex = Math.floor(Math.random() * currentIndex);
    currentIndex--;

    // And swap it with the current element.
    [numberArray[currentIndex], numberArray[randomIndex]] = [
      numberArray[randomIndex], numberArray[currentIndex]];
  }
// Used like so
  //console.log(numberArray);
  return numberArray;

}

function FindZero(board){
  //finds next 0 on board and returns its location//
  let zeroPlacement = [];
    for(let i = 0; i < board.length; i++){
      for(let j = 0; j < board[i].length; j++){
        if(board[i][j] === 0){
          zeroPlacement.push(i);
          zeroPlacement.push(j);
          return zeroPlacement;
          }
          if(zeroPlacement.length !== 0){
            return null;
          }
      }
    }
    return null;
}

function IsValidPlacement(row,col,value, board){
  // 3 if statements, nr. 1 checks for horizontal duplicates. Nr 2 = Vertical duplicates. Nr 3 = 3x3 block duplicated
   for(let i = 0; i < board.length; i++){
    
     if (board[row][i] === value)
         {
           return false;
         }
     if (board[i][col] === value)
         {
           return false;
         }
     if (board[Math.floor((row - row % 3) + i / 3)][Math.floor((col - col % 3) + i % 3)] === value)
         {
           return false;
         }
   }
   return true;
 }

  return(
    <>
