import {React} from "react";
import Dropdown from "./DropDown";
import Timer from "./Timer"
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faRotateLeft, faLightbulb,faFaceDizzy } from '@fortawesome/free-solid-svg-icons'

//  <FontAwesomeIcon icon={faLightbulb} />


function Controls(props){
  
  

  async function handleDifficulty(e){
    // using async function to get SetDifficultyLevel to change so it triggers a new generateBoard() in SudokuBrain
    if(e.target.id === "easy"){
      await props.SetDifficultyLevel(0);
      props.SetDifficultyLevel(1);
    } else if (e.target.id === "medium"){
      await props.SetDifficultyLevel(0);
      props.SetDifficultyLevel(2);
    } else if(e.target.id === "hard"){
      await props.SetDifficultyLevel(0);
      props.SetDifficultyLevel(3);

    }
    console.log(props.difficultyLevel)
    
  }

  function handleNumberClick(e){
    
    let ref = props.lastRef.current;
   
     if(isNaN(parseInt(props.liveBoard[ref.id])) && e.target.innerText !== "erase"){
      props.lastRef.current.innerText = e.target.innerText;
    }
      props.lastRef.current.focus()
    props.checkSolution();
  }

  function undo(e){
    // Animation for undo Arrow
    let arrow = document.querySelector(".undoButton .icon");
    arrow.classList.toggle("iconTransition")

    // Get last used ref to know what to undo
    let ref = props.lastRef.current;

    if(parseInt(ref.innerText) !== props.liveBoard[ref.id]){
      props.lastRef.current.innerText = ""
    }
    props.lastRef.current.focus()
    props.checkSolution();
  }

  function hint(e){
    
  }

  function inputKeys(){
    return(
      <div className="inputContainer">
        <div value="1" className="inputKey" onClick={handleNumberClick}>1</div>
        <div className="inputKey" onClick={handleNumberClick}>2</div>
        <div className="inputKey" onClick={handleNumberClick}>3</div>
        <div className="inputKey" onClick={handleNumberClick}>4</div>
        <div className="inputKey" onClick={handleNumberClick}>5</div>
        <div className="inputKey" onClick={handleNumberClick}>6</div>
        <div className="inputKey" onClick={handleNumberClick}>7</div>
        <div className="inputKey" onClick={handleNumberClick}>8</div>
        <div className="inputKey" onClick={handleNumberClick}>9</div>
        <div  className="inputKey undoButton" onClick={undo}><FontAwesomeIcon className="icon"  onClick={undo} icon={faRotateLeft} /><span className="iconButtons">Undo</span></div>
        <div className="inputKey"><FontAwesomeIcon className="icon" icon={faLightbulb}/><span className="iconButtons">Hint</span></div>
        <div className="inputKey"><FontAwesomeIcon className="icon" icon={faFaceDizzy} /><span className="iconButtons">Solve</span></div>
      </div>
    )
  }
  let inputkeyList = inputKeys();
  return(
    <div className="controlContainer">
    <div className="dropdownContainer">
    <Dropdown handleDifficulty={handleDifficulty}/>
  </div>  
  
  <Timer 
  SetScoreCard={props.SetScoreCard}
  scoreCard={props.scoreCard}
  isSolved={props.isSolved}
  switchTheme={props.switchTheme}
  difficultyLevel={props.difficultyLevel}

  />
    {inputkeyList}
    </div>
  )
}

export default Controls;
