import {React,useState,useEffect} from "react";

function Dropdown() {
  const [isActive, setIsActive] = useState(false);

  function handleDifficulty(e){
    console.log(e.target.value)
    if(e.target.value === "easy"){
      props.SetDifficultyLevel(1);
    } else if (e.target.value === "medium"){
      props.SetDifficultyLevel(2);
    } else if(e.target.value === "Hard"){
      props.SetDifficultyLevel(3);

    }
    console.log(props.difficultyLevel)
    generateBoard();
  }

  function handleClick(){
    const DropdownDiv = document.getElementById("drop");
    document.addEventListener("click", (event) => {
      const isClickInside = DropdownDiv.contains(event.target);
    
      if (!isClickInside) {
        setIsActive(false)
      }
    });

  }

  useEffect(()=>{
handleClick()
  }, [])
  return (
    <div id="drop" className='dropdown'>
    <div onClick={e => setIsActive(!isActive)} className="dropdown-btn">
      New Game
    </div>
    { isActive && (
      <div className='dropdown-content'>
        <div className='dropdown-item'>
        Easy
        </div>
        <div className='dropdown-item'>
          Medium
        </div>
        <div className='dropdown-item'>
          Hard
        </div>
      </div>
    )}
    </div>
  );
}

export default Dropdown;
