import React from "react";
import { useContext } from "react";
import { UserContext } from "../userContext";
import '../components/styles.css'

function SolvedScreen(){
  const {difficultyLevel,isSolved,scoreCard} = useContext(UserContext)

const level = () => {
  let levelName = "hmmmmm";
  if(difficultyLevel === 1){
    levelName = "Easy"
  } else if(difficultyLevel === 2){
    levelName = "Medium";
  } else if(difficultyLevel === 3){
    levelName = "Hard";
  }
  return(
    <h1>{levelName}</h1>
  )
}

let levelname = level();

  if(isSolved){

  
  return(
    <div className="solvedCardContainer">
    <div className="solvedCardWrapper">
      <h1 >Congratulations</h1>
      <h2>You solved the puzzle</h2>
        <span className="solvedCardTime">{("0" + Math.floor((scoreCard.time / 60000) % 60)).slice(-2)}:</span>
        <span className="solvedCardTime">{("0" + Math.floor((scoreCard.time / 1000) % 60)).slice(-2)}</span>
         {levelname}
         <h1>{difficultyLevel}</h1>

    </div>
      

    </div>
  )
  }
  return <></>
}

export default SolvedScreen;
