import {React} from "react";
import Dropdown from "./DropDown";
import Timer from "./Timer"
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faRotateLeft, faLightbulb,faFaceDizzy } from '@fortawesome/free-solid-svg-icons'

//  <FontAwesomeIcon icon={faLightbulb} />


function Controls(props){
  
  

  async function handleDifficulty(e){
    // using async function to get SetDifficultyLevel to change so it triggers a new generateBoard() in SudokuBrain
    if(e.target.id === "easy"){
      await props.SetDifficultyLevel(0);
      props.SetDifficultyLevel(1);
    } else if (e.target.id === "medium"){
      await props.SetDifficultyLevel(0);
      props.SetDifficultyLevel(2);
    } else if(e.target.id === "hard"){
      await props.SetDifficultyLevel(0);
      props.SetDifficultyLevel(3);

    }
    console.log(props.difficultyLevel)
    
  }

  function handleClick(e){
    console.log(e.target);
    let ref = props.lastRef.current;
    if(e.target.innerText === "erase" && parseInt(ref.innerText) !== props.liveBoard[ref.id]){
      props.lastRef.current.innerText = ""
    } if(isNaN(parseInt(props.liveBoard[ref.id])) && e.target.id !== "erase"){
      props.lastRef.current.innerText = e.target.innerText;
    }
   // if(e.target.innerText === "erase" && ref.innerText !== props.liveBoard[ref.id]){
   //   props.lastRef.current.innerText = ""
   // }else {
   //   props.lastRef.current.innerText = e.target.innerText;
   // }
// props.liveBoard
    props.lastRef.current.focus()
    props.checkSolution();
  }

  function inputKeys(){
    return(
      <div className="inputContainer">
        <div value="1" className="inputKey" onClick={handleClick}>1</div>
        <div className="inputKey" onClick={handleClick}>2</div>
        <div className="inputKey" onClick={handleClick}>3</div>
        <div className="inputKey" onClick={handleClick}>4</div>
        <div className="inputKey" onClick={handleClick}>5</div>
        <div className="inputKey" onClick={handleClick}>6</div>
        <div className="inputKey" onClick={handleClick}>7</div>
        <div className="inputKey" onClick={handleClick}>8</div>
        <div className="inputKey" onClick={handleClick}>9</div>
        <div id="Undo" className="inputKey" onClick={handleClick}><FontAwesomeIcon icon={faRotateLeft} /></div>
        <div className="inputKey"><FontAwesomeIcon icon={faLightbulb}/></div>
        <div className="inputKey"><FontAwesomeIcon icon={faFaceDizzy} /></div>
      </div>
    )
  }
  let inputkeyList = inputKeys();
  return(
    <div className="controlContainer">
    <div className="dropdownContainer">
    <Dropdown handleDifficulty={handleDifficulty}/>
  </div>  
  
  <Timer 
  SetScoreCard={props.SetScoreCard}
  scoreCard={props.scoreCard}
  isSolved={props.isSolved}
  switchTheme={props.switchTheme}
  difficultyLevel={props.difficultyLevel}

  />
    {inputkeyList}
    </div>
  )
}

export default Controls;
