import React, {useState} from "react";
import useLocalStorage from 'use-local-storage'
import SudokuBrain from "./components/SudokuBrain";
import SudokuBoard from "./components/SudokuBoard";
import SolvedScreen from "./components/SolvedScreen";
import '../src/components/styles.css'


function App() {
  const [solvedBoard,SetSolvedBoard] = useState([])
  const [difficultyLevel, SetDifficultyLevel] = useState(1)
  const [currentBoard, SetCurrentBoard] = useState([])
  const [userAttempt, SetUserAttempt] = useState([])
  const [darkTheme, SetDarkTheme] = useState(false)
  const [currentRef, SetCurrentRef] = useState(null)
  const [isSolved,SetIsSolved] = useState(false);
  const [scoreCard,SetScoreCard] = useState({time:0})

  return (
    <div className={darkTheme ? "dark-theme" : "light-theme"}>
    <div className="title"><h1>Sudoku Maniac</h1></div>
    <div className="">

    <SolvedScreen
      isSolved={isSolved}
      scoreCard={scoreCard}
      difficultyLevel={difficultyLevel}
    />

      <div className="sudokuBoardWrapper">
      <div>
      <SudokuBoard
        solvedBoard={solvedBoard}
        difficultyLevel={difficultyLevel}
        SetDifficultyLevel={SetDifficultyLevel}
        userAttempt={userAttempt}
        SetUserAttempt={SetUserAttempt}
        currentBoard={currentBoard}
        SetCurrentRef={SetCurrentRef}
        SetIsSolved={SetIsSolved}
        isSolved={isSolved}
        SetScoreCard={SetScoreCard}
        scoreCard={scoreCard}
        isSolved={isSolved}

      />
      </div>

      <div className="controlWrapper">


      <SudokuBrain
        solvedBoard={solvedBoard}
        SetSolvedBoard={SetSolvedBoard}
        SetDifficultyLevel={SetDifficultyLevel}
        difficultyLevel={difficultyLevel}
        currentBoard={currentBoard}
        SetCurrentBoard={SetCurrentBoard}
        SetIsSolved={SetIsSolved}
      />
      </div>
    </div>
      </div>

      <button onClick={() => SetDarkTheme(!darkTheme)}>dark theme</button>
    </div>
  );
}

export default App;
